import { useCookies } from "react-cookie";

function useUserInfo() {
  const [cookies, setCookie] = useCookies(["userInfo"]);

  return [
    cookies["userInfo"] || {},
    (obj) => typeof obj === "object" && setCookie("userInfo", obj),
  ];
}

export default useUserInfo;
